import React, { useState } from 'react';
import { v1 } from 'uuid';
import './App.css';
import { Todolist } from './Todolist';

export type FilterValuesType = string
type TodolistType = {
    id: string
    title: string
    filter: FilterValuesType
}




function App() {

    let todolistID1=v1()
    let todolistID2=v1()

    
    const [todolists, setTodolists] = useState<Array<TodolistType>>([
        { id: todolistID1, title: 'What to learn', filter: 'All' },
        { id: todolistID2, title: 'What to buy', filter: 'All' },
    ])

        const [tasks, setTasks] = useState({
        [todolistID1]:[
            { id: v1(), title: "CSS", isDone: true },
            { id: v1(), title: "JS", isDone: true },
            { id: v1(), title: "React", isDone: false },
            { id: v1(), title: "Rest Api", isDone: false },
            { id: v1(), title: "GraphQL", isDone: false },
        ],
        [todolistID2]:[
            { id: v1(), title: "CSS", isDone: true },
            { id: v1(), title: "JS", isDone: true },
            { id: v1(), title: "React", isDone: false },
            { id: v1(), title: "Rest Api", isDone: false },
            { id: v1(), title: "GraphQL", isDone: false },
            ],
    })


    // const [todolists, setTodolists] = useState<Array<TodolistType>>([
    //     { id: v1(), title: 'What to learn', filter: 'All' },
    //     { id: v1(), title: 'What to buy', filter: 'All' },
    // ])




    // const [tasks, setTasks] = useState([
    //     { id: v1(), title: "CSS", isDone: true },
    //     { id: v1(), title: "JS", isDone: true },
    //     { id: v1(), title: "React", isDone: false },
    //     { id: v1(), title: "Rest Api", isDone: false },
    //     { id: v1(), title: "GraphQL", isDone: false },
    // ])

    // // const [filterButton, setFilterButton] = useState<FilterValuesType>('All')


    
    const buttons = [
        { id: v1(), title: 'All' },
        { id: v1(), title: 'Completed' },
        { id: v1(), title: 'Active' }
    ]

    const removeTasks = (todolistID:string, id: string) => {
        let filtered = tasks[todolistID].filter(el => el.id !== id)
        setTasks({...tasks, [todolistID]: tasks[todolistID].filter(el => el.id !== id) })
    }

    const addTask = (newTask: string) => {
        let newTitle = { id: v1(), title: newTask, isDone: false }
        setTasks([newTitle, ...tasks]);
    }


    const taskFilter = (todolistID:string, filterValue: FilterValuesType) => {
        setTodolists(todolists.map(el=>el.id === todolistID ? {...el, filter : filterValue } : el))
    }

    const changeCheckboxStatus = (todolistID:string, currentId: string, currentEvent: boolean) => {
        setTasks(tasks.map(el => el.id === currentId ? { ...el, isDone: currentEvent } : el))
    }


    // let prokladka = tasks
    // if (filterButton === 'Active') {
    //     prokladka = tasks.filter(el => el.isDone === false)
    // }
    // if (filterButton === 'Completed') {
    //     prokladka = tasks.filter(el => el.isDone === true)
    // }


    return (
        <div className="App">
            {todolists.map((el) => {
                    let prokladka = tasks[el.id]
                    if (el.filter === 'Active') {
                        prokladka = tasks[el.id].filter(el => el.isDone === false)
                    }
                    if (el.filter === 'Completed') {
                        prokladka = tasks[el.id].filter(el => el.isDone === true)
                    }

                return (
                    <Todolist
                        key={el.id}
                        todolistID={el.id}
                        title={el.title}
                        tasks={tasks}
                        removeTasks={removeTasks}
                        taskFilter={taskFilter}
                        prokladka={prokladka}
                        addTask={addTask}
                        changeCheckboxStatus={changeCheckboxStatus}
                        filterButton={el.filter}
                        buttons={buttons}
                    />)
            })
            }
        </div>
    )
}



export default App;
