import React from "react";
import style from '../Todolist.module.css'
import { FilterValuesType } from '../App';
import {useState} from "react";

type UniButtonType = {
    filterButton: FilterValuesType
    filterHandler: (filterValue: FilterValuesType)=>void
}



export const UniButton = (props: UniButtonType) => {

    const [buttonTitle, setButtonTitle] = useState([
        {title: 'All'},
        {title: 'Completed'},
        {title: 'Active'}

    ])

    const filterHandlerButton = (title: string) => {
        props.filterHandler(title)
    }

    let unicButton = buttonTitle.map((button) => {
        return(<button className={props.filterButton === "All" ? style.activeFilter : ''} onClick={() => filterHandlerButton(button.title)}>{button.title}</button>)})

    return (
        {unicButton}
    )
}
