import { type } from 'os';
import React from 'react';
import { useState } from 'react';

export type TaskType = {
    title: string;
    id: number;
    isDone: boolean;
}

type PropsType = {
    title: string;
    tasks: Array<TaskType>;
    removeTasks: (id: number) => void;
    //taskFilter: (filterValue: string)=>void;
}

export function Todolist(props: PropsType) {

    const [filterButton, setFilterButton] = useState('All')
    const taskFilter = (filterValue: string) => {
        setFilterButton(filterValue)
    }

    let prokladka = props.tasks
    if (filterButton === 'Active') {
        prokladka = props.tasks.filter(el => el.isDone === false)
    }
    if (filterButton === 'Complited') {
        prokladka = props.tasks.filter(el => el.isDone === true)
    }

    return (
        <div className="App">
            <div>
                <h3>{props.title}</h3>
                <div>
                    <input />
                    <button>+</button>
                </div>
                <ul>
                    {prokladka.map((el: TaskType) => {
                        return (
                            <li key={el.id}>
                                <input type="checkbox" checked={el.isDone}></input>
                                <span>{el.title}</span>
                                <button onClick={() => {props.removeTasks(el.id)}}>X</button>
                            </li>
                        )
                    })}
                </ul>
                <div>
                    <button onClick={()=>taskFilter('All')}>All</button>
                    <button onClick={()=>taskFilter('Active')}>Active</button>
                    <button onClick={()=>taskFilter('Complited')}>Completed</button>
                </div>
            </div>
        </div>
    );
}
