import React, {useState, KeyboardEvent} from "react";

type EditableSpanType = {
    title: string
}

export const EditableSpan = (props:EditableSpanType) => {

    let [inputOrSpan, setInputOrSpan] = useState(false)

    const onDoubleClickHandler = () => {
        setInputOrSpan(true)
    }

    const onKeyPressHadler = (event: KeyboardEvent<HTMLInputElement>) => {
        if (event.key === 'Enter') {
            setInputOrSpan(false)
        }
    }

    if (inputOrSpan === true) {return(<input onDoubleClick={onDoubleClickHandler} value={props.title}/>)} return  <span onKeyPress={onKeyPressHadler}>{props.title}</span>

