import React, { useState, KeyboardEvent } from "react";

type EditableSpanType = {
    title: string
}

export const EditableSpan = (props: EditableSpanType) => {

    let [inputOrSpan, setInputOrSpan] = useState(false)

    const onDoubleClickHandler = () => {
        setInputOrSpan(true)
    }

    const onKeyPressHadler = (event: KeyboardEvent<HTMLInputElement>) => {
        if (event.key === 'Enter') {
            setInputOrSpan(false)
        }
    }

    const onBlurHandler = (event: KeyboardEvent<HTMLInputElement>) => {
        if (event.key === 'Enter') {
            setInputOrSpan(false)
        }
    }

    return (
    inputOrSpan
    ? <input onKeyPress={onKeyPressHadler} onBlur={onBlurHandler} value={props.title} />
         : <span onDoubleClick={onDoubleClickHandler} >{props.title}</span>
    )
}
