import { type } from 'os';
import React from 'react';

export type TaskType = {
    title: string;
    id: number;
    isDone: boolean;
}

type PropsType = {
    title: string;
    tasks: Array<TaskType>;
}

export function Todolist(props: PropsType) {
    return (
        <div className="App">
            <div>
                <h3>{props.title}</h3>
                <div>
                    <input />
                    <button>+</button>
                </div>
                <ul> 
{props.tasks.map((el:TaskType, id)=>{
    <li><input type="checkbox"></input><span>{props.title}</span></li>
})}
                </ul>
                <div>
                    <button>All</button>
                    <button>Active</button>
                    <button>Completed</button>
                </div>
            </div>
        </div>
    );
}


























