import React from "react";
import style from '../Todolist.module.css'
import { FilterValuesType } from '../App';

type UniButtonType = {
    filterButton: FilterValuesType
    filterHandler: (filterValue: FilterValuesType)=>void
}



export const UniButton = (props: UniButtonType) => {

    const filterHandlerButton = () => {
        props.filterHandler()
    }


    return (
        <button className={props.filterButton === "All" ? style.activeFilter : ''} onClick={() => filterHandler('All')}>All</button>
    )
}
